    private void Btn1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn1ActionPerformed
String str1 = (String)itemCB.getSelectedItem();
int res = JOptionPane.showConfirmDialog(null,"Are you sure to remove all details of "+str1+" from the database?");
if(res==JOptionPane.YES_OPTION){
    try{
Class.forName("java.sql.Driver");
Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/kashyap", "root", "kvalwar");
Statement stmt = con.createStatement();
String query = "DELETE FROM lab WHERE ItemName="+"'"+str1+"';";
stmt.executeUpdate(query);
JOptionPane.showMessageDialog(null,"Record "+str1+" "+"is deleted!");
}
catch(Exception e){
JOptionPane.showMessageDialog(null,"Error in connectivity");
}
}
itemCB.removeItem(str1);
itemCB.setSelectedIndex(0);
pTF.setText("");
lTF.setText("");
qTF.setText("");
daTF.setText("");// TODO add your handling code here:
}//GEN-LAST:event_Btn1ActionPerformed

