    private void itemCBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_itemCBActionPerformed
String str1 = (String)itemCB.getSelectedItem();
try{
Class.forName("java.sql.Driver");
Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/kashyap", "root", "kvalwar");
Statement stmt = con.createStatement();
String query = "SELECT * FROM lab WHERE ItemName="+"'"+str1+"';";
ResultSet rs = stmt.executeQuery(query);
if ( rs.next()){
String price = rs.getString(3);
String life = rs.getString(4);
String  qty= rs.getString(5);
String dep = rs.getString(6);
String  date = rs.getString(7);
pTF.setText(price);
lTF.setText(life);
qTF.setText(qty);
daTF.setText(date);
Btn1.setEnabled(true);
}
}
catch(Exception e){
JOptionPane.showMessageDialog(null,"Error in connectivity");
}// TODO add your handling code here:
    }//GEN-LAST:event_itemCBActionPerformed

    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
try {
Class.forName("com.mysql.jdbc.Driver");
System.out.print("\nConnecting to database...");
Connection connection = DriverManager.getConnection("jdbc:mysql://localhost/kashyap", "root", "kvalwar");
System.out.println(" SUCCESS!\n");
Statement statement = connection.createStatement();
String query = "SELECT * FROM lab";
ResultSet rs = statement.executeQuery(query);
while (rs.next())
{
String name = rs.getString("ItemName");
itemCB.addItem(name);
}
connection.close();
itemCB.setEnabled(true);
Btn1.setEnabled(true);
}
catch (Exception e) {
e.printStackTrace();
}        // TODO add your handling code here:
    }//GEN-LAST:event_formWindowOpened

